export default class CapMonster {
    apiKey: string;
    constructor(apiKey: string);
    createTask(type: string, websiteURL: string, websiteKey: string, isInvisible?: boolean): Promise<number | undefined>;
    getTaskResult(taskId: number): Promise<{
        success: boolean;
        solution: string;
    }>;
    awaitTaskResult(taskId: number): Promise<{
        success: boolean;
        solution: string;
    } | {
        success: boolean;
        solution?: undefined;
    }>;
}
