/**
 * 커스텀 오류 클래스입니다.
 * 오류가 발생할 경우 Error 대신 이 클래스가 반환됩니다.
 */
export default class CulturelandError {
    _name;
    _message;
    error; // optional getter가 존재하지 않아 그냥 접근할 수 있도록 하였음.
    /**
     * 커스텀 오류 클래스입니다.
     * @param name 오류 이름
     * @param message 오류 메시지
     * @param error 원본 오류 (오류가 AxiosError인 경우)
     */
    constructor(name, message, error) {
        this._name = name;
        this._message = message;
        this.error = error;
    }
    /**
     * 오류 이름
     */
    get name() {
        return this._name;
    }
    /**
     * 오류 메시지
     */
    get message() {
        return this._message;
    }
    /**
     * @example
     * `${name}: ${message}`
     */
    toString() {
        return `${this._name}: ${this._message}`;
    }
}
