/**
 * 핀번호를 관리하는 클래스입니다.
 * 핀번호 포맷팅에 사용됩니다.
 */
export default class Pin {
    private _parts;
    /**
     * 핀번호를 자동으로 포맷팅합니다.
     * 핀번호가 유효하지 않은 경우 parts가 null을 반환합니다.
     * @param pin 상품권의 핀번호
     */
    constructor(pin: string);
    /**
     * 핀번호의 유효성을 검증합니다.
     * 존재할 수 없는 핀번호를 검사하여 불필요한 요청을 사전에 방지합니다.
     * @param pin 상품권의 핀번호
     * @returns `false` - 핀번호가 유효하지 않음
     * @returns `string[]` - 핀번호를 4자리씩 끊은 배열 | `["3110", "0123", "4567", "8901"]`
     */
    static format(pin: string): [string, string, string, string] | false;
    /**
     * 포맷팅이 완료된 핀번호입니다.
     * 유효하지 않은 핀번호가 입력되었을 경우 null을 반환합니다.
     * @example
     * ["3110", "0123", "4567", "8901"]
     */
    get parts(): [string, string, string, string] | null;
    /**
     * 핀번호를 string으로 반환합니다.
     * @example
     * "3110-0123-4567-8901"
     */
    toString(): string;
}
